import React,{useState} from 'react';
import BtnRender from './BtnRender';
import axios from 'axios';
import Loading from '../loading/Loading';

function ProductItem({ product,setProducts, isAdmin, token, callback, setCallback }) {

  const [loading, setLoading] = useState(false);

  const deleteProduct = async () => {
    console.log(product)
    try {
      setLoading(true);
      const destroyImg = axios.post('/api/destroy', { public_id: product.images.public_id }, {
        headers:{Authorization:token}
      });


      const deleteProduct = axios.delete(
        `/api/products/${product._id}`,
        {
          headers: { Authorization: token }
        }
      );


      await destroyImg
      await deleteProduct
      setLoading(false);
      setCallback(!callback);

    } catch (error) {
      console.log(error);
      alert(error.response.data.msg);
    }
  }

  const handleCheck = () => {
    let newProduct = [...product];
    newProduct.checked = !newProduct.checked;
    setProducts(newProduct);
  }

  if(loading) return <div className='product_card'><Loading/></div>

  return (
    <div className="product_cart">
      {
        isAdmin && <input type="checkbox" checked={product.checked} onChange={handleCheck}/>
      }
      <img src={product.images.url} alt="" />

      <div className="product_box">
              <h2 title={product.title}>{product.title}</h2>
              <span>${product.price}</span>
              <p>{product.description}</p>
      </div>

      <BtnRender product={product} deleteProduct={deleteProduct}/>
    </div>
  );
}

export default ProductItem;
